int8_t	cmdGetStartIndex	( uint8_t *rx )					
{
	uint8_t index;
	for( index = 0 ; index < 255 ; index++ )
	{
		if ( rx[index] == '-' && rx[index+1] == '+' )
		{
			return index;
		}
	}

	return -1; // Kein Kommando gefunden
}

